<template>
  <md-card
    :style="style"
    class="login-form">
    <md-card-content>
      <div
        :style="formLogo"
        class="logo"/>
      <md-field>
        <label>Username / Email</label>
        <md-input v-model="credentials.account_name"/>
      </md-field>
      <md-field>
        <label>Password</label>
        <md-input
          v-model="credentials.password"
          type="password"/>
      </md-field>
    </md-card-content>
    <md-card-actions>
      <login-button
        :to="{ name: 'base.install' }"
        class="md-primary md-raised"
        @click="onClickInstall">Install</login-button>
      <login-button
        :to="{ name: 'base.dashboard' }"
        class="md-primary md-raised"
        @click="onClickLogin">Login</login-button>
    </md-card-actions>
  </md-card>
</template>

<script>
export default {
  name: 'LoginForm',
  components: {
    'login-button': () => import('@partials/base-button')
  },
  props: {
    logo: {
      type: String,
      default: `static/svg/logo.svg`
    },
    width: {
      type: Number,
      default: 400
    },
    height: {
      type: Number,
      default: 350
    }
  },
  data () {
    return {
      credentials: {
        account_name: '',
        password: ''
      },
      style: {},
      formLogo: {}
    }
  },
  mounted () {
    this.style = {
      'border-radius': '0 !important',
      'position': 'absolute',
      'width': this.width + 'px',
      'height': this.height + 'px',
      'top': 'calc(50% - ' + (this.height / 2) + 'px)',
      'left': 'calc(50% - ' + (this.width / 2) + 'px)'
    }
    const datauri = `data:image/svg+xml;base64,`
    if (this.logo) {
      this.formLogo = { backgroundImage: `url(` + datauri + `)`, backgroundSize: 'cover' }
    }
  },
  methods: {
    onClickLogin () {
      this.$emit('login', this.credentials)
    },
    onClickInstall () {
      this.$emit('install')
    }
  }
}
</script>

<style lang="scss" scoped>
  .login-form {
    background-color: rgba(white, .7) !important
  }
  .login-form .md-field.md-theme-default > .md-icon::after {
    background: transparent !important;
  }
  .login-form .logo {
    text-align: center;
    height: 110px;
    width: 368px;
    display: block;
  }
  .login-form .logo img {
    margin: 0 auto;
  }
  .login-form .md-button {
    border-radius: 0;
  }
</style>
